import Header from "../Header";
import Footer from "../Footer";

import React, { useState } from "react";

function Login() {

  const [errorMessages, setErrorMessages] = useState({});
  const [isSubmitted, setIsSubmitted] = useState(false);

  const database = [
    {
      username: "user1",
      password: "pass1"
    },
    {
      username: "user2",
      password: "pass2"
    }
  ];

  const errors = {
    uname: "Invalid Username: Username does not exist.",
    pass: "Invalid Password: Password is incorrect for this user."
  };

  const handleSubmit = (event) => {
    var { uname, pass } = document.forms[0];
    var status;

    let data = {Username: uname.value, Password: pass.value};
    status = fetch('/check', {
        method: 'POST',
        // We convert the React state to JSON and send it as the POST body
        body: JSON.stringify(data),
        headers: {
        'Content-Type': 'application/json'
       } 
      }).then(function(response) {
        return response.json();
      });

    status.resolve();
    status.then(value => {
      console.log(value);
    }).catch(err => {
      console.log(err);
    });

    const userData = database.find((user) => user.username === uname.value);

    if (userData) {
      if (userData.password !== pass.value) {
        setErrorMessages({ name: "pass", message: errors.pass });
      } else {
        setIsSubmitted(true);
      }
    } else {
      setErrorMessages({ name: "uname", message: errors.uname });
    }

    event.preventDefault();
  };

  const renderErrorMessage = (name) =>
    name === errorMessages.name && (
      <div className="error">{errorMessages.message}</div>
    );

  const renderForm = (
    <div className="form">
      <form onSubmit={handleSubmit}>
        <div>
          <label className="input-label">Username: </label>
          <input type="text" name="uname" required />
          {renderErrorMessage("uname")}
        </div>
        <div>
          <label className="input-label">Password: </label>
          <input type="password" name="pass" required />
          {renderErrorMessage("pass")}
        </div>
        <div className="button-container">
          <input type="submit" value="Submit" />
        </div>
      </form>
    </div>
  );

  return (
    <div>
      <Header />
      <div className="login-form">
        <div className="login-header padding-top-64">
          <h1>Sign In</h1>
        </div>
        {isSubmitted ? <div className="login-success">User is successfully logged in [insert home page here]</div> : renderForm}
      </div>
      <Footer />
    </div>
  );
}

export default Login;
